import React, {useState, useEffect} from 'react';
import axios from 'axios';
import { set } from 'mongoose';
function App(){
const [transaction, setTransactions] = useState([]);

// New State for the form
const [formData, setFormData] = useState({
description: '',
amount: '',
category: '',
date: new Date().toISOString().split('T')[0], // Default to today
});

const deleteTransaction = async (id) => {
    if(!window.confirm("Are you sure you want to delete this transaction?")){
        return;
    }
    try{
        await axios.delete(`http://localhost:8080/api/transactions/${id}`);
        fetchTransactions(); // Refresh the list
    }catch(error){
        console.error("Error deleting transaction: ", error);
    }
};

// This runs when the page loads
useEffect(() => {
fetchTransactions();}, []);
const fetchTransactions = async () => {
try{
const response = await axios.get('http://localhost:8080/api/transactions');
setTransactions(response.data);
}
catch(error){
console.error("Error fetching data: ", error);
}
};
const handleInputChange = (e) => {
    setFormData({
        ...formData,
        [e.target.name]: e.target.value,
    });
}

const handleFormSubmit = async (e) => {
    e.preventDefault();
    try{
        await axios.post('http://localhost:8080/api/transactions', formData);
        setFormData({
            description: '',
            amount: '',
            category: '',
            date: new Date().toISOString().split('T')[0],
        });
        fetchTransactions(); // Refresh the list
    }catch(error){
        console.error("Error adding transaction: ", JSON.stringify(error.response.data));
    }
};

return(
<div style={{padding: '20px', maxWidth: '800px', margin: '0 auto'}}>
<h1>My Expense Tracker</h1>
<form onSubmit={handleFormSubmit} style={{marginBottom: '30px', display: 'flex',gap: '10px', flexWrap: 'wrap'}}>
<input name='description' type='text' placeholder='Description' value={formData.description} onChange={handleInputChange} required />
<input name='amount' type='number' placeholder='Amount' value={formData.amount} onChange={handleInputChange} required />
<input name='category' type='text' placeholder='Category' value={formData.category} onChange={handleInputChange} required />
<input name='date' type='date' placeholder='Date' value={formData.date} onChange={handleInputChange} required />
<button type='submit'>Add Transaction</button>
</form>
</div>
);



return (
<div style={{padding: '20px'}}>
<h1>My Expense Tracker</h1>
<table border="1" cellPadding="10" cellSpacing="0">
<thead>
<tr>
<th>Description</th>
<th>Amount</th>
<th>Category</th>
<th>Date</th>
</tr>
</thead>
<tbody>
{transaction.map((txn) => (
<tr key={txn.id}>
<td>{txn.description}</td>
<td>{txn.amount}</td>
<td>{txn.category}</td>
<td>{new Date(txn.date).toLocaleDateString()}</td>
</tr>
))}
</tbody>
</table>
</div>
);
}

export default App;
